import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './login.css';

async function loginUser({ name, pass }) {
  return fetch('http://localhost:4000/auth/login', {
    method: 'POST', // or 'GET' based on the server
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ name, pass }) // Ensure this matches backend structure
  })
  .then(response => {
    if (!response.ok) {
      throw new Error('Login failed');
    }
    return response.json();
  });
}

export default function Login({ setToken }) {
  const [username, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = await loginUser({
        name: username,
        pass: password
      });
      setToken(token); // Assumes token is an object, adjust if necessary
    } catch (error) {
      setError('Failed to log in. Please try again.');
    }
  };

  return (
    <div className="login-wrapper">
      <h1>Please Log In</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Username</p>
          <input type="text" value={username} onChange={e => setUserName(e.target.value)} />
        </label>
        <label>
          <p>Password</p>
          <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
        </label>
        <div>
          <button type="submit">Submit</button>
        </div>
        {error && <p className="error">{error}</p>} {/* Display error if login fails */}
      </form>
    </div>
  );
}

Login.propTypes = {
  setToken: PropTypes.func.isRequired
};
