import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import CartPage from './pages/CartPage';
import ProductListPage from './pages/ProductListPage';
import AddProductPage from './pages/AddProductPage';
import EditProductPage from './pages/EditProductPage';
import DetailsProductPage from './pages/DetailsProductPage';
import LoginPage from './component/LoginPage/LoginPage';

function setToken(userToken) {
  sessionStorage.setItem('token', JSON.stringify(userToken));
}

function getToken() {
  const tokenString = sessionStorage.getItem('token');
  const userToken = JSON.parse(tokenString);
  return userToken?.token;
}

function App() {
  const token = getToken();

  return (
    <Router>
      <Routes>
        <Route path="/login" element={!token ? <LoginPage setToken={setToken} /> : <Navigate to="/" />} />
        <Route path="/" element={token ? <ProductListPage /> : <Navigate to="/login" />} />
        <Route path="/productlist" element={token ? <ProductListPage /> : <Navigate to="/login" />} />
        <Route path="/product/add" element={token ? <AddProductPage /> : <Navigate to="/login" />} />
        <Route path="/product/edit/:productId" element={token ? <EditProductPage /> : <Navigate to="/login" />} />
        <Route path="/product/details/:productId" element={token ? <DetailsProductPage /> : <Navigate to="/login" />} />
        <Route path="/cart" element={token ? <CartPage /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
