import { useState, useRef, useEffect } from "react";
import { fetchProductsBatch } from "../../services/ProductService";
import ItemCard from '../comom/ItemCard/ItemCard.js';
import { useNavigate } from "react-router-dom";

export default function ItemCardScroll() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [hasEndingPosts, setHasEndingPosts] = useState(false);
  const [loading, setLoading] = useState(false); // Add loading state
  const loaderRef = useRef(null);
  const navigate = useNavigate();

  const handleDetailsClick = (productId) => {
    navigate(`/product/details/${productId}`);
  };


  useEffect(()=> {
    const loadMoreProducts = async()=>{
      setLoading(true);
      const newProducts  = await fetchProductsBatch(currentPage,3);
      setProducts([...products,...newProducts]);
      setLoading(false);
    }

    loadMoreProducts();


  },[currentPage,products]);

  const lastProduct = useCallback(
    (node) => {
      if (loading) return;
      if (loaderRef.current) loaderRef.current.disconnect();

      loaderRef.current = new IntersectionObserver((entries) => {
        if (entries[0].isIntersecting) {
          setPage((prevPage) => prevPage + 1); 
        }
      });

      if (node) loaderRef.current.observe(node);
    },
    [loading]
  );




  return (
    <div>
        {products.map((product, index) => (
          <div>
            <ItemCard
              key={index}
              title={product.name}
              description={product.description}
              price={product.price}
              imageUrl={product.image_url}
              handleClick={() => handleDetailsClick(product.id)}
            />
            <div ref={product.length === index + 1 ? lastProduct: null}></div>
          </div>
        ))}
    </div>
  );
}
