import React, { useEffect, useState } from "react";
import { fetchCartCurrent } from "../../services/CartService";
import { getUser } from "../../utils/AuthUtils";
import { fetchItemsByCart } from "../../services/ItemService";

function CartPanel() {
  const [cart, setCart] = useState(null); 
  const [items, setItems] = useState([]); 

  useEffect(() => {
    const fetchCart = async () => {
        try {
          const response = await fetchCartCurrent(getUser().id); 
          setCart(response);
        } catch (error) {
          console.error("Error fetching cart:", error);
        }
      };

    const fetchItemsOfCart = async () => {
        try {
            const date = new Date(cart.date_created);            
            const year = date.getFullYear();
            const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
            const day = String(date.getDate()).padStart(2, '0');
            const hours = String(date.getHours()).padStart(2, '0');
            const minutes = String(date.getMinutes()).padStart(2, '0');
            const seconds = String(date.getSeconds()).padStart(2, '0');
            const cart_date = `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;

          const response = await fetchItemsByCart(getUser().id, cart.date_created); 
          setItems(response);
        } catch (error) {
          console.error("Error fetching cart:", error);
        }
      };

    fetchCart();
    fetchItemsByCart();

  }, []);

  if (!cart) {
    return <p>Loading cart...</p>; 
  }

  return (
    <div>
      <p>Cart ID: {cart.id_user}</p>
      <p>Date Created: {cart.date_created}</p>
      <p>Date Sold: {cart.date_sold ? cart.date_sold : "Not sold yet"}</p>
      <div>
        {items.map((item,index)=>(
            <div    key={index}>
                <p>{item.id}</p>
                <p>{item.quantity}</p>
            </div>
        ))}
      </div>
    </div>

  );
}

export default CartPanel;
