import React, { useEffect, useState } from "react";
import { fetchCartCurrent } from "../../services/CartService";
import { getUser } from "../../utils/AuthUtils";
import { fetchItemsByCart } from "../../services/ItemService";
import { formatDate } from "../../utils/commomUtils";

function CartPanel() {
  const [user, setUser] = useState(getUser()); 
  const [cart, setCart] = useState(""); 
  const [items, setItems] = useState([]); 


  useEffect(() => {
    const fetchCart = async () => {
        try {
          const response = await fetchCartCurrent(getUser().id); 
          setCart(response);

          const cartDate  = cart.date_created
          const response2 = await fetchItemsByCart(user.id, "2024-07-14 09:00:02")
          setItems(response2)
      }catch(error){
        console.error("error fetching items",error);
      }
    }

    fetchCart();
    console.log("cartdate++===="+ cart.date_created)
    fetchItemsInCart()
  }, []);

  if (!cart) {
    return <p>Loading cart...</p>; 
  }

  return (
    <div>
      <p>Cart ID: {cart.id_user}</p>
      <p>Date Created: {cart.date_created}</p>
      <p>Date Sold: {cart.date_sold ? cart.date_sold : "Not sold yet"}</p>

      <div>
        {items.map((item,index)=>(
          <div>
            <p>{item.id}</p>
            <p>{item.id_user}</p>
            <p>{item.cart_date}</p>
          </div>
        ))}
      </div>
    </div>

  );
}

export default CartPanel;
