import React, { useEffect, useState } from "react";
import { fetchCartCurrent } from "../../services/CartService";
import { getUser } from "../../utils/AuthUtils";

function CartPanel() {
  const [cart, setCart] = useState(null); // Initialize as null to represent unloaded state

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await fetchCartCurrent(getUser().id); // Fetch the cart data
        setCart(response);
      } catch (error) {
        console.error("Error fetching cart:", error);
      }
    };

    fetchCart();
  }, []); // Empty dependency array to only run on component mount

  if (!cart) {
    return <p>Loading cart...</p>; // Display loading state while cart is being fetched
  }

  return (
    <div>
      <p>Cart ID: {cart.id}</p>
      <p>Date Created: {cart.date_created}</p>
      <p>Date Sold: {cart.date_sold ? cart.date_sold : "Not sold yet"}</p>
    </div>
  );
}

export default CartPanel;
