import axios from 'axios';


export const fetchAllCarts = async () => {
  try {
    const response = await axios.get(`http://localhost:4000/api/cart`);
    return response.data;
  } catch (error) {
    console.error('Error fetching product:', error);
    throw error;
  }
};

export const fetchCartCurrent = async (userId) => {
  try {
    const response = await axios.get(`http://localhost:4000/api/cart/current`,{userId});
    return response.data;
  } catch (error) {
    console.error('Error fetching product:', error);
    throw error;
  }
};


export const postCart = async (userId) => {
  try {
    const response = await axios.post(`http://localhost:4000/api/cart`, {userId});
    console.log(response.data)
    return response.data;
  } catch (error) {
    console.error('Error posting product:', error);
    throw error;
  }
};
