import { useState, useRef, useEffect } from "react";
import { fetchProductsBatch } from "../../services/ProductService";
import ItemCard from '../comom/ItemCard/ItemCard.js'
import { useNavigate } from "react-router-dom";
import { all } from "axios";

export default function ItemCardScroll({ allProducts }) {
    const [products, setProducts] = useState(allProducts);
    const [currentPage, setCurrentPage] = useState(2);
    const [hasEndingPosts, setHasEndingPosts] = useState(false);
    const loaderRef = useRef(null);
    const navigate = useNavigate();

    const handleDetailsClick = (productId) => {
        navigate(`/product/details/${productId}`);
      };

    useEffect(() => {
        if (hasEndingPosts) return;
        const observer = new IntersectionObserver((entities) => {
          const target = entities[0];

          if (target.isIntersecting) {
            setCurrentPage(old => old + 1);
          }
        }, 1);

        if (loaderRef.current) {
          observer.observe(loaderRef.current);
        }

        return () => observer.disconnect(); // Clean up observer
      }, [hasEndingPosts]);


      useEffect(() => {
        const handleRequest = async () => {
          try {
            const data = await fetchProductsBatch(currentPage, 3);
            console.log(data)

            if (!data.length) {
              setHasEndingPosts(true);
              return;
            }

            setProducts(prevProducts => [...(prevProducts || []), ...data]);
          } catch (error) {
            console.error('Error fetching products:', error);
          }
        };

        handleRequest();
      }, [currentPage]);


    return (
      <div>
        <section >

            {products.map((product,index)=>(
                <ItemCard
                key={index}
                title={product.name }
                description={product.description}
                price={product.price}
                imageUrl={product.image_url}
                handleClick = {() => handleDetailsClick(product.id)}
            />
            ))}

          <p ref={loaderRef}>Carregando mais episodios...</p>
        </section>
      </div>
    )
  }
