import { useState, useRef, useEffect } from "react";
import { fetchProductsBatch } from "../../services/ProductService";
import ItemCard from '../comom/ItemCard/ItemCard.js';
import { useNavigate } from "react-router-dom";

export default function ItemCardScroll() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [hasEndingPosts, setHasEndingPosts] = useState(false);
  const [loading, setLoading] = useState(false); // Add loading state
  const loaderRef = useRef(null);
  const navigate = useNavigate();

  const handleDetailsClick = (productId) => {
    navigate(`/product/details/${productId}`);
  };

  useEffect(() => {
    if (hasEndingPosts || loading) return; // Avoid fetch if already loading or if all posts are loaded

    const observer = new IntersectionObserver((entries) => {
      const target = entries[0];
      if (target.isIntersecting) {
        setCurrentPage((prevPage) => prevPage + 1);
      }
    }, {
      root: null,
      rootMargin: '0px',
      threshold: 1.0
    });

    if (loaderRef.current) {
      observer.observe(loaderRef.current);
    }

    return () => {
      if (loaderRef.current) {
        observer.disconnect();
      }
    };
  }, [hasEndingPosts, loading]);

  useEffect(() => {
    const handleRequest = async () => {
      setLoading(true); // Set loading to true before fetching
      try {
        const data = await fetchProductsBatch(currentPage, 3);

        if (data.length === 0) {
          setHasEndingPosts(true);
        } else {
          setProducts((prevProducts) => [...prevProducts, ...data]);
        }
      } catch (error) {
        console.error("Error fetching products:", error);
      } finally {
        setLoading(false); // Reset loading state after fetching
      }
    };

    handleRequest();
  }, [currentPage]);

  return (
    <div>
      <section>
        {products.map((product, index) => (
          <ItemCard
            key={index}
            title={product.name}
            description={product.description}
            price={product.price}
            imageUrl={product.image_url}
            handleClick={() => handleDetailsClick(product.id)}
          />
        ))}

        {!hasEndingPosts && !loading && (
          <p ref={loaderRef}>Carregando mais produtos...</p>
        )}

        {hasEndingPosts && !loading && (
          <p>Nenhum outro produto a ser carregado</p>
        )}
      </section>
    </div>
  );
}
